return items.map(item => {
  const data = item.json;
  const hourly = data.hourly;

  // Extract hourly values
  const temps = hourly.temperature_2m;
  const rain = hourly.rain;
  const snow = hourly.snowfall;
  const wind = hourly.wind_speed_10m;
  const soil0_1 = hourly.soil_moisture_0_to_1cm;
  const soil1_3 = hourly.soil_moisture_1_to_3cm;
  const soil3_9 = hourly.soil_moisture_3_to_9cm;

  // Calculate stats
  const tempHigh = Math.max(...temps).toFixed(1);
  const tempLow = Math.min(...temps).toFixed(1);
  const totalRain = rain.reduce((sum, val) => sum + val, 0).toFixed(2);
  const totalSnow = snow.reduce((sum, val) => sum + val, 0).toFixed(2);
  const windMax = Math.max(...wind).toFixed(1);
  const windMin = Math.min(...wind).toFixed(1);

  const avg = arr => (arr.reduce((sum, v) => sum + v, 0) / arr.length).toFixed(3);
  const soil_0_1_avg = avg(soil0_1);
  const soil_1_3_avg = avg(soil1_3);
  const soil_3_9_avg = avg(soil3_9);

  // Step-by-step report format
  const message = 
